import React, { useState } from 'react'
import { Link } from 'react-router-dom'
const Navbar = () => {


    let disableLoginBtn = false
    let disableSigninBtn = true

    const disableBtn = (val) => {
        if (val === 1) {
            document.getElementById("signin").style.display = "none";
            document.getElementById("login").style.display = "hidden";
            disableLoginBtn = true
            disableSigninBtn = false
        } else {
            document.getElementById("signin").style.display = "hidden";
            document.getElementById("login").style.display = "none";
            disableLoginBtn = false
            disableSigninBtn = true
        }
    }

    return (

        <nav className="navbar navbar-expand-lg fixed-top navbar-light shadow-sm p-1 mb-5">
            <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarText"
                aria-controls="navbarText" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse " id="navbarText">
                <ul className="navbar-nav ml-auto " >
                    <li className="nav-item">
                        <Link className="nav-link font-weight-bold text-dark margin-right-2em"
                            to="/login" 
                            id="login"
                            >
                            <button className="btn btn-dark" onClick={disableBtn(1)} vi={disableLoginBtn}>
                                Log in
                                </button>
                        </Link>
                    </li>
                    <li className="nav-item">

                        <Link className="nav-link font-weight-bold text-dark margin-right-2em"
                            id="signin"
                            to="/">
                            <button className="btn btn-dark" onClick={disableBtn(0)}
                                disabled={disableSigninBtn}>Sign in</button> </Link>

                    </li>
                </ul>
            </div>
        </nav>
    )
}

export default Navbar;
