import axios from 'axios'

export function busSearch({from, to, trvDate}){
    // let apiUrl = `http://localhost:8080/bus/search`
    let apiUrl = `http://localhost:8080/search/bus?q=${from}-${to}-${trvDate}`



    return axios.get(apiUrl)
}
