import React, { useEffect } from 'react'
import { Link } from 'react-router-dom'
const Navbar = () => {


    const [disabLog, setDisab] = useEffect(false)
    const [disabSignin, setDisabSignin] = useEffect(true)


    const disableBtn = (val) => {
        if (val === 1) {
            //login
            setDisabSignin(false)
            setDisab(true)
        } else {
            setDisab(false)
            setDisabSignin(true)
        }
    }

    return (

        <nav className="navbar navbar-expand-lg fixed-top navbar-light shadow-sm p-1 mb-5">
            <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarText"
                aria-controls="navbarText" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse " id="navbarText">
                <ul className="navbar-nav ml-auto " >
                    <li className="nav-item">
                        <Link className="nav-link font-weight-bold text-dark margin-right-2em"
                            onClick={disableBtn(1)}
                            to="/login"><button className="btn btn-dark" disabled={disabLog}> Log in </button></Link>
                    </li>
                    <li className="nav-item">
                        <button className="btn btn-dark" disabled={disabSignin}>
                            <Link className="nav-link font-weight-bold text-dark margin-right-2em"
                                onClick={disableBtn(0)}
                                to="/"> Sign in </Link></button>

                    </li>
                </ul>
            </div>
        </nav>
    )
}

export default Navbar;
