import React, { useState } from 'react'
import { useSelector } from 'react-redux'
import * as seatsApi from '../api/seat-reserve'

const SeatReservation = (props) => {

    let selectedSeats = useSelector(state => state.seats.selectedSeats) || []
    const [busNumber, setBusnumber] = useState(props.busNumber)
    const [noOfSeats, setNoOfSets] = useState([])

    const seatData = {
        busNumber: busNumber,
        seats: selectedSeats
    }

    const seats = () =>{

        for (let index = 1; index < 40; index++){
            setNoOfSets(index)           
        }
        return
    }
    seats()
    console.log("seats", noOfSeats)



    const handleSubmitEvent = (seatData) => {
        seatsApi.seatReserve(seatData)
            .then(response => response.data)
            .then(data => {
                //...
                console.log(data)
            })
    }

    const renderNameAgeFields = () => {
        return selectedSeats.map(item => {
            return (
                <li className="list-group-item card-op card card-body" key={item}>
                    <span className="badge badge-dark card-op">{item}</span>
                    <div className="row">
                        <div className="col-9 ">
                            <input className="form-control card-op" placeholder="Name" />
                        </div>
                        <div className="col-3 ">
                            <input className="form-control card-op" placeholder="Age" />
                        </div>
                    </div>
                </li>
            )
        })
    }

    return (
        <div>
            <h4 className="text-white font-weight-bold">
                Selected seats : {selectedSeats.join(",")}
            </h4>
            <ul>
                {renderNameAgeFields()}
            </ul>
            <hr />
            <button onClick={e => handleSubmitEvent()} className="btn btn-dark">Checkout</button>
        </div>
    );
};

export default SeatReservation;
