import axios from 'axios'

export function emailAuth(user){
    let apiUrl = 'http://localhost:8080/signup'

    const email = {'email':user}
    return axios.post(apiUrl,email,{
        headers:{
            'Content-Type':'application/json'
        }
    })
}
