import React, { useState } from 'react'

const Login = () => {

    let [user, setUser] = useState({
        name: '',
        email: '',
        mobileno: '',
        password: '',
        dob: '',
        gender: ''
    })
    let [message, setMessage] = useState({})
    let [btnDisable,setBtnDisable] = useState("")


    const handleChangeEvent = (e, field) => {
        let fieldValue = e.target.value
        setUser({ ...user, [field]: fieldValue })
        console.log(user)

        if (user.name.length === 0) {
            setMessage({ ...message, name: "Name Required" })
        } else if (user.email.length === 0) {
            setMessage({ ...message, email: "Email Required" })

        } else if (user.mobileno.length === 0) {
            setMessage({ ...message, mobileno: "Mobile No Required" })
        } else if (user.password.length === 0) {
            setMessage({ ...message, password: "Password Required" })
        } else if (user.dob.length === 0) {
            setMessage({ ...message, dob: "DOB Required" })
        } else if (user.gender.length === 0) {
            setMessage({ ...message, gender: "Gender Required" })
        }

    }
    const handleSubmitEvent = e => {
        e.preventDefault()

    }

    return (

        <div className="row ">
            <div className="col-6 offset-md-3">
                <div className="card row col-6 offset-md-3 card-op margin-top-6em">
                    <div className="card-body">
                        <form>
                            <div className="form-group">
                                <label className="text-dark">Name</label>
                                <input className="form-control card-op" onChange={e => handleChangeEvent(e, 'name')} />
                                <div id="name-feedback" className="text-danger">{message.name}</div>
                            </div>
                            <div className="form-group">
                                <label className="text-dark">Email</label>
                                <input className="form-control card-op" onChange={e => handleChangeEvent(e, 'email')} />
                                <div id="email-feedback" className="text-danger">{message.email}</div>

                            </div>
                            <div className="form-group">
                                <label className="text-dark">Mobile No.</label>
                                <input className="form-control card-op" onChange={e => handleChangeEvent(e, 'mobile')} />
                                <div id="mobile-feedback" className="text-danger">{message.mobileno}</div>

                            </div>
                            <div className="form-group">
                                <label className="text-dark">Password</label>
                                <input type="password" className="form-control card-op" onChange={e => handleChangeEvent(e, 'password')} />
                                <div id="password-feedback" className="text-danger">{message.password}</div>

                            </div>


                            <div className="form-group">
                                <label className="text-dark">DOB</label>
                                <input className="form-control card-op" onChange={e => handleChangeEvent(e, 'dob')} />
                                <div id="dob-feedback" className="text-danger">{message.dob}</div>

                            </div>
                            <div className="form-group">
                                <label className="text-dark">Gender</label> <br />
                                <input type="radio" className="card-op" value="male" name="gender" onChange={e => handleChangeEvent(e, 'gender')} /> Male &nbsp;
                            <input type="radio" className="card-op" value="female" name="gender" onChange={e => handleChangeEvent(e, 'gender')} /> Female
                            <div id="gender-feedback" className="text-danger">{message.gender}</div>
                            </div>
                            <div className="text-center">
                                <button className="btn btn-dark p-2" disabled={btnDisable}>Create Account</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div >
    )
}

export default Login;
