import React, { useState } from 'react'
import { useSelector } from 'react-redux'
import * as seatsApi from '../api/seat-reserve'

const SeatReservation = (props) => {


    let selectedSeats = useSelector(state => state.seats.selectedSeats) || []
    const [busNumber, setBusnumber] = useState(props.busNum)
    const [fare, setFare] = useState(props.fare)


    const seatData = {
        busNumber: busNumber,
        seats: selectedSeats
    }

    let total = fare * selectedSeats.length + 60


    const renderTotalFare = (fare, total) => {
        if (selectedSeats.length > 0) {
            return (
                <table>
                    <tbody>
                        <td className="text-white font-weight-bold">Seat Fare: {fare}</td>
                        <td className="text-white font-weight-bold">Service tax: 60</td>

                        <td className="text-white font-weight-bold">Total Fare: {total}</td>
                    </tbody>
                </table>
            )
        }
        return
    }



    const handleSubmitEvent = (seatData) => {
        seatsApi.seatReserve(seatData)
            .then(response => response.data)
            .then(data => {
                console.log(data)
            })
        props.tab(3)
    }

    const renderNameAgeFields = () => {
        if (selectedSeats.length > 0) {
            return selectedSeats.map(item => {
                return (
                    <li className="list-group-item card-op card card-body" key={item}>
                        <span className="badge badge-dark card-op">{item}</span>
                        <div className="row">
                            <div className="col-9 ">
                                <input className="form-control card-op" placeholder="Name" />
                            </div>
                            <div className="col-3 ">
                                <input className="form-control card-op" placeholder="Age" />
                            </div>
                        </div>
                    </li>
                )
            })
        }
        return
    }

    return (
        <div>
            <h4 className="text-white font-weight-bold">
                Selected seats : {selectedSeats.join(",")}
            </h4>
            <ul>
                {renderNameAgeFields()}
            </ul>
            <ul>
                {renderTotalFare(fare, total)}
            </ul>
            <button onClick={e => handleSubmitEvent()} className="btn btn-dark">Checkout</button>
        </div>
    );
};

export default SeatReservation;
