import axios from 'axios'

export function busSearch({ from, to, trvDate }) {
    let apiUrl = `http://localhost:8080/bus/search`
    return axios.get(apiUrl, {
        headers: {
            Authorization:
                `to "LUCKNOW"`,
            Authorization: `trvDate "22/6/2020"`,
            Authorization: `from "BARABANKI"`

        }
    })
}
